    /**
     * @param args the command line arguments
     */
    public static void main(String args[]) {
        final int ANCHO = 800, ALTO = 450, CAPACIDAD = 10;
        ColaFrame frame = new ColaFrame();
        CanvasCola canvas = new CanvasCola(CAPACIDAD);
        ColaLenta cola = new ColaLenta(10, canvas);
        
        Productor hProd[] = new Productor[4];
        for (int i = 0; i < 4; i++)
            hProd[i] = new Productor(cola);
        Thread hConsu = new Thread(new Consumidor(cola));
        
        canvas.setSize(ANCHO, ALTO);
        frame.setTitle("RepresentaciÃ³n de la cola lenta");
        frame.setSize(ANCHO, ALTO);
        frame.setLocationRelativeTo(null);
        frame.add(canvas);
        frame.setVisible(true);
        
        for (int i = 0; i < 4; i++)
            hProd[i].start();
        hConsu.start();
        
        try {
            for (int i = 0; i < 4; i++)
                hProd[i].join();
            hConsu.join();
            Thread.sleep(3000);
        } catch (Exception ex) {
            ex.printStackTrace();
        }
        
        System.out.println("practica3.ColaFrame.main() END");
    }


